int get_floor(const char *floor) {
    int result = 0;
    while(*floor) {
        if(*floor == '(')
            ++result;
        else if(*floor == ')')
            --result;
        ++floor;
    }

    return result;
}

int basement_idx(const char* floor) {
    int result = 0, idx = 0;
    while(*floor && result >= 0) {
        if(*floor == '(')
            ++result;
        else if(*floor == ')')
            --result;
        ++floor;
        ++idx;
    }
    return idx;
}

